mapper.select:
select * from (
	select rownum r, b_no, b_id, b_name, b_title, b_content, b_date, b_hit, m_photo 
	from (select * from HR_BBS, HR_MEMBER where b_id = m_id order by b_no)
) where r <= #{start} and r >= #{end} 
order by r desc

select * from HR_REPLY 
where b_no = #{b_no} 
order by rp_date
